//import imgg from '../../storage/hellothisisimage.png'
import React from "react";
import "./style.css";
import { Helmet, HelmetProvider } from "react-helmet-async";
import { Container, Row, Col } from "react-bootstrap";
import { dataportfolio, meta } from "../../content_option";

export const Portfolio = () => {
  const handleClick = (link) => {
    // Handle the click event, e.g., navigate to the specified link
    window.open(link, '_blank');
  };
  return (
    <HelmetProvider>
  <Container className="About-header" style="font-family: Arial, sans-serif; margin: 0; padding: 0;">
    <Helmet>
      <meta charSet="utf-8" />
      <title>Portfolio | {meta.title}</title>{" "}
      <meta name="description" content={meta.description} />
    </Helmet>
    <Row className="mb-5 mt-3 pt-md-3">
      <Col lg="8">
        <h1 className="display-4 mb-4" style="font-size: 2.5rem; margin-bottom: 1rem;">Portfolio</h1>{" "}
        <hr className="t_border my-4 ml-0 text-left" style="border-top: 2px solid #000; margin: 1rem 0;" />
      </Col>
    </Row>
    <div>
      {dataportfolio.map((data, i) => {
        return (
          <div
            key={i}
            style="display: flex; flex-direction: column; align-items: center; text-align: center; margin-bottom: 3.5rem;"
          >
            <h3 style="margin-bottom: 3.5rem;">{data.title}</h3>
            <div style="display: flex; flex-direction: row;">
              <img
                src={data.img}
                alt=""
                style="width: 40%; margin-bottom: 0.5rem;"
              />
              <div style="text-align: justify; width: 60%; margin-left: 5rem;">
                <p>{data.description}</p>
              </div>
            </div>
          </div>
        );
      })}
    </div>
  </Container>
</HelmetProvider>

  );
};
